function resultToPace(result) {
    //round up!
    var seconds = Math.ceil(result.time / 100);
    var distance = result.race.racetype.miles;

    var m = Math.floor((seconds / 60) / distance);

    var s = Math.round(((((seconds / 60) / distance) % 1) * 60));
    if (s === 60) {
        m = m + 1;
        s = 0;
    }

    if (s < 10) s = "0" + s;
    return m + ":" + s;
}

